static void DumpInitializers(FILE* output, RuleEntry *r, char * pReturn)
{
    char *p = pReturn;
    char *pDataType;
    char *pSymbol;
    char *pEqualSign;
    char *pValue;
    char *pSeparator;
    int nest = 0;
    char *q;

    require(pReturn!=NULL, "DumpInitializer: invalid string");

    while (*p != 0) {
        p = endFormal(p,
                &pDataType,
                &pSymbol,
                &pEqualSign,
                &pValue,
                &pSeparator,
                &nest);
        if (nest != 0) return;
        if (pValue != NULL) {
            tab();
            q = strBetween(pSymbol, pEqualSign, pSeparator);
            fprintf(output, "_retv.%s", q);
            q = strBetween(pValue, NULL, pSeparator);
            fprintf(output, " = %s;\n", q);
        }
    }
}

static void DumpFormals(FILE* output, char * pReturn, int bInitializer)
{
    char *p = pReturn;
    char *pDataType;
    char *pSymbol;
    char *pEqualSign;
    char *pValue;
    char *pSeparator;
    int nest = 0;
    char *q;
    int count = 0;

    require(pReturn!=NULL, "DumpFormals: invalid string");

    while (*p != 0) {
        p = endFormal(p,
                &pDataType,
                &pSymbol,
                &pEqualSign,
                &pValue,
                &pSeparator,
                &nest);
        if (nest != 0) return;
        if (count > 0) fprintf(output,",");
        if (pDataType != NULL && pSymbol != NULL) {
            q = strBetween(pDataType, pSymbol, pSeparator);
            fprintf(output, "%s", q);
            q = strBetween(pSymbol, pEqualSign, pSeparator);
            fprintf(output," %s",q);
            if (pValue != NULL) {
                q = strBetween(pValue, NULL, pSeparator);
                if (bInitializer != 0) {
                    fprintf(output, " = %s", q);
                }
            }
        }
        count++;
    }
}

/**
 * Check for empty alt in a more intelligent way.
 * Previously, an empty alt for genBlk had to point directly
 * to the endBlock.  This did not work once I changed {...}
 * blocks to look like (...|...| epsilon) since there were
 * intervening generics.  This fixes the problem for this
 * particular case.  Things like actions or empty blocks of
 * various kinds will still cause problems, but I wasnt't
 * prepared to handle pathological cases like (A|()*). It
 * does handle (A | ()), which is a recommended idiom for
 * epsilon.
 *
 * Actually, this isn't quite correct since it doesn't handle
 * the case of the ignore bit in the plus block bypass, but
 * I'm too tired to figure out the correct fix, and will just
 * work around it.
 */
static int isEmptyAlt(Node * alt, Node * endBlock)
{
  Node * n = alt;
  Junction * j;
  while (n != endBlock) {
    switch (n->ntype) {

      case nRuleRef:
        return 0;

      case nToken:
        return 0;

      case nAction:
        return 0;

      case nJunction:
        goto JUNCTION;

      default:
        fatal_internal("Invalid node type");
        return 0;
    }
JUNCTION:
    j = (Junction *) n;

    switch (j->jtype) {
      case Generic:
        {
          n = j->p1;
          goto NEXT;
        }

      case aSubBlk:
        {
          n = j->p1;
          goto NEXT;
        }

      case EndBlk:
          return 0;

      case EndRule:
          return 1;

      default:
          return 0;
    }
NEXT: continue;
  }
  return 1;
}
